"""\nAPI Request Examples\nDemonstrating various HTTP methods and API interactions\n"""

import requests
import json

# Base URL for JSONPlaceholder API (free fake API for testing)
BASE_URL = "https://jsonplaceholder.typicode.com"

# ===== GET Request Example =====\ndef get_example():\n    """Retrieve data from API"""\n    print("\\n=== GET Request Example ===")\n    \n    # Get all users\n    response = requests.get(f"{BASE_URL}/users")\n    \n    print(f"Status Code: {response.status_code}")\n    print(f"Headers: {dict(response.headers)}")\n    \n    if response.status_code == 200:\n        users = response.json()\n        print(f"\\nFirst 3 users:")\n        for user in users[:3]:\n            print(f"  - {user['name']} ({user['email']})")\n    \n    # Get specific user\n    user_id = 1\n    response = requests.get(f"{BASE_URL}/users/{user_id}")\n    if response.status_code == 200:\n        user = response.json()\n        print(f"\\nUser {user_id}: {user['name']}")\n\n# ===== POST Request Example =====\ndef post_example():\n    """Create new resource"""\n    print("\\n=== POST Request Example ===")\n    \n    new_post = {\n        "title": "My New Post",\n        "body": "This is the content of my post",\n        "userId": 1\n    }\n    \n    response = requests.post(\n        f"{BASE_URL}/posts",\n        json=new_post,\n        headers={"Content-Type": "application/json"}\n    )\n    \n    print(f"Status Code: {response.status_code}")\n    \n    if response.status_code == 201:\n        created_post = response.json()\n        print(f"Created Post ID: {created_post['id']}")\n        print(f"Title: {created_post['title']}")\n\n# ===== PUT Request Example =====\ndef put_example():\n    """Update entire resource"""\n    print("\\n=== PUT Request Example ===")\n    \n    updated_post = {\n        "id": 1,\n        "title": "Updated Title",\n        "body": "Updated content",\n        "userId": 1\n    }\n    \n    response = requests.put(\n        f"{BASE_URL}/posts/1",\n        json=updated_post,\n        headers={"Content-Type": "application/json"}\n    )\n    \n    print(f"Status Code: {response.status_code}")\n    \n    if response.status_code == 200:\n        updated = response.json()\n        print(f"Updated Post: {updated['title']}")\n\n# ===== PATCH Request Example =====\ndef patch_example():\n    """Partially update resource"""\n    print("\\n=== PATCH Request Example ===")\n    \n    partial_update = {\n        "title": "Partially Updated Title"\n    }\n    \n    response = requests.patch(\n        f"{BASE_URL}/posts/1",\n        json=partial_update,\n        headers={"Content-Type": "application/json"}\n    )\n    \n    print(f"Status Code: {response.status_code}")\n    \n    if response.status_code == 200:\n        updated = response.json()\n        print(f"Updated field: {updated['title']}")\n\n# ===== DELETE Request Example =====\ndef delete_example():\n    """Delete resource"""\n    print("\\n=== DELETE Request Example ===")\n    \n    response = requests.delete(f"{BASE_URL}/posts/1")\n    \n    print(f"Status Code: {response.status_code}")\n    \n    if response.status_code == 200:\n        print("Resource deleted successfully")\n\n# ===== Query Parameters Example =====\ndef query_params_example():\n    """Using query parameters for filtering"""\n    print("\\n=== Query Parameters Example ===")\n    \n    # Filter posts by userId\n    params = {"userId": 1}\n    response = requests.get(f"{BASE_URL}/posts", params=params)\n    \n    if response.status_code == 200:\n        posts = response.json()\n        print(f"Found {len(posts)} posts for user 1")\n        print(f"First post: {posts[0]['title']}")\n\n# ===== Error Handling Example =====\ndef error_handling_example():\n    """Handle API errors gracefully"""\n    print("\\n=== Error Handling Example ===")\n    \n    try:\n        # Request non-existent resource\n        response = requests.get(f"{BASE_URL}/users/9999")\n        response.raise_for_status()  # Raises HTTPError for bad status\n        \n    except requests.exceptions.HTTPError as e:\n        print(f"HTTP Error: {e}")\n        print(f"Status Code: {response.status_code}")\n        \n    except requests.exceptions.ConnectionError:\n        print("Connection Error: Unable to reach API")\n        \n    except requests.exceptions.Timeout:\n        print("Timeout Error: Request took too long")\n        \n    except requests.exceptions.RequestException as e:\n        print(f"General Error: {e}")\n\n# ===== Authentication Example =====\ndef auth_example():\n    """Example with authentication headers"""\n    print("\\n=== Authentication Example ===")\n    \n    # Note: This is a demo; JSONPlaceholder doesn't require auth\n    headers = {\n        "Authorization": "Bearer YOUR_API_TOKEN_HERE",\n        "Content-Type": "application/json"\n    }\n    \n    response = requests.get(f"{BASE_URL}/posts/1", headers=headers)\n    print(f"Status Code: {response.status_code}")\n    print("Headers sent with authentication token")\n\nif __name__ == "__main__":\n    print("=====================================")\n    print("API Request Examples")\n    print("=====================================")\n    \n    get_example()\n    post_example()\n    put_example()\n    patch_example()\n    delete_example()\n    query_params_example()\n    error_handling_example()\n    auth_example()\n    \n    print("\\n=====================================")\n    print("All examples completed!")\n    print("=====================================")\n
